    function ifScript() {		
		var rec = new GlideRecord('x_172976_term_shee_term_sheet_tasks');
		rec.addQuery('parent', current.sys_id); 
		rec.addQuery('state', 4);
		rec.query();
		if (rec.next()) {
			return 'no';
		} else {
			return 'yes';
		}	
	}

